package day05_math_operators;

public class Remainder {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
String word = "Tsolmon";
String newName = "";
for (int i=0; i< word.length(); i++) {
	newName = newName + word.charAt(i);
	System.out.println(newName);
	
}

		
		
	}

}








