package day06_operators2;

public class IncrementDecrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int employees = 50;
employees++;
employees+=1;
employees = employees + 1;
System.out.println(employees);
employees--;
--employees;
employees-=1;
employees=employees-1;
System.out.println(employees);
	}

}
